    <script>
        function toggleActive(sel, boolean) {
            var el = document.getElementById(sel),
                all = el.getElementsByTagName('input'),
                i;
            for (i = 0; i < all.length; i++) {
                all[i].disabled = boolean;
                if(boolean){
                    all[i].parentElement.classList.add('opacity-30')
                }else{
                    all[i].parentElement.classList.remove('opacity-30')
                }
            }
        }
        var dbCheckSelector = document.getElementById('dbCheck');
        if(dbCheckSelector.length){
            document.getElementById('dbCheck').onchange = function () {
                toggleActive('dbSetup', this.checked);
            }
        }
    </script>
